mapper.select:
SELECT t1.f_id, CASE WHEN t1.f_nickname IS NOT NULL THEN f_name || '(' || t1.f_nickname || ')' ELSE t1.f_name END AS f_name, t1.f_name AS F_VALUE, t1.f_name AS NAME , t2.user_name AS f_parent_id, t1.f_tel, t1.f_address, t1.f_idcard, t1.f_point, t1.f_nickname, t1.f_type, t1.f_status FROM t_customer t1 INNER JOIN (SELECT id, user_name FROM t_user WHERE status = '1' AND expiring_date >= sysdate START WITH id = #{userId} CONNECT BY PRIOR id = parent_id) t2 ON t1.f_parent_id = t2.id WHERE 1 = 1
AND t1.f_name like '%' || #{customer.name} || '%'
AND t1.f_nickname like '%' || #{customer.nickName} || '%'
AND t1.f_type = #{customer.type}
AND t1.f_status = #{customer.status}
